import React, { useState } from "react";
import styled from "styled-components";
import TextInput from "./TextInput";
import Button from "./Button";
import { useDispatch } from "react-redux";
import { UserSignIn } from "../api";
import { loginSuccess } from "../redux/reducers/UserSlice";
import { openSnackbar } from "../redux/reducers/SnackbarSlice";

const Container = styled.div`
  width: 10px;
  max-width: 40px; /* Reasonable width for a login form */
  padding: 40px 20px;
  margin: 0 auto; /* Center the container */
  display: flex;
  flex-direction: column;
  gap: 36px;
  background-color: ${({ theme }) => theme.background || "#fff"}; /* Background color */
  border-radius: 8px; /* Rounded corners */
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1); /* Subtle shadow for depth */
`;

const Title = styled.div`
  font-size: 24px; /* Smaller than before to fit better */
  font-weight: 700; /* Slightly less bold for better readability */
  color: ${({ theme }) => theme.primary};
  text-align: center; /* Center the title */
`;

const Span = styled.div`
  font-size: 14px; /* Slightly smaller for subtext */
  font-weight: 400;
  color: ${({ theme }) => theme.text_secondary || "#666"};
  text-align: center; /* Center the subtext */
`;

const TextButton = styled.div`
  width: 100%;
  text-align: end;
  color: ${({ theme }) => theme.text_primary || "#000"};
  cursor: pointer;
  font-size: 14px;
  transition: all 0.3s ease;
  font-weight: 500;
  &:hover {
    color: ${({ theme }) => theme.primary || "#007bff"};
  }
`;

const SignIn = ({ setOpenAuth }) => {
  const dispatch = useDispatch();
  const [loading, setLoading] = useState(false);
  const [buttonDisabled, setButtonDisabled] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const validateInputs = () => {
    if (!email || !password) {
      alert("Please fill in all fields");
      return false;
    }
    return true;
  };

  const handelSignIn = async () => {
    setLoading(true);
    setButtonDisabled(true);
    if (validateInputs()) {
      try {
        const res = await UserSignIn({ email, password });
        dispatch(loginSuccess(res.data));
        dispatch(
          openSnackbar({
            message: "Login Successful",
            severity: "success",
          })
        );
        setOpenAuth(false);
      } catch (err) {
        dispatch(
          openSnackbar({
            message: err.message,
            severity: "error",
          })
        );
      } finally {
        setLoading(false);
        setButtonDisabled(false);
      }
    } else {
      setLoading(false);
      setButtonDisabled(false);
    }
  };

  return (
    <Container>
      <div>
        <Title>Welcome to Krist ðŸ‘‹</Title>
        <Span>Please login with your details here</Span>
      </div>
      <div style={{ display: "flex", gap: "20px", flexDirection: "column" }}>
        <TextInput
          label="Email Address"
          placeholder="Enter your email address"
          value={email}
          handelChange={(e) => setEmail(e.target.value)}
        />
        <TextInput
          label="Password"
          placeholder="Enter your password"
          password
          value={password}
          handelChange={(e) => setPassword(e.target.value)}
        />

        <TextButton>Forgot Password?</TextButton>
        <Button
          text="Sign In"
          onClick={handelSignIn}
          isLoading={loading}
          isDisabled={buttonDisabled}
        />
      </div>
    </Container>
  );
};

export default SignIn;
